// Initialize health check session
const checkId = `health-${Date.now()}`;
const timestamp = new Date().toISOString();

console.log(`Starting Wazuh API health check: ${checkId}`);

return {
  check_id: checkId,
  start_time: timestamp,
  wazuh_server: '172.20.18.14:55000',
  check_type: 'scheduled_health_monitoring'
};

// Process health check results
const initData = $('Initialize Health Check').first().json;
const healthResponse = $('Check Wazuh Health').first().json;
const wazuhResponse = $('Check Wazuh Connectivity').first().json;
const clusterResponse = $('Get Cluster Status').first().json;

const endTime = new Date().toISOString();
const responseTime = Date.now() - new Date(initData.start_time).getTime();

// Analyze Wazuh API health
const wazuhHealth = {
  status: healthResponse?.status || (healthResponse ? 'healthy' : 'unknown'),
  api_accessible: !!healthResponse,
  response_time_ms: responseTime
};

// Analyze Wazuh manager status
const managerStatus = {
  status: wazuhResponse?.data?.status || 'unknown',
  version: wazuhResponse?.data?.version || 'unknown',
  compilation_date: wazuhResponse?.data?.compilation_date || null,
  api_response_time: responseTime
};

// Analyze cluster status
const clusterStatus = {
  enabled: clusterResponse?.data?.enabled || false,
  running: clusterResponse?.data?.running || 'unknown',
  nodes: clusterResponse?.data?.nodes || []
};

// Determine overall health status
let overallStatus = 'healthy';
const issues = [];

if (!wazuhHealth.api_accessible) {
  overallStatus = 'critical';
  issues.push('Wazuh API is not accessible');
}

if (managerStatus.status !== 'running') {
  overallStatus = 'degraded';
  issues.push('Wazuh manager is not running');
}

if (responseTime > 5000) {
  overallStatus = 'warning';
  issues.push('Slow API response time detected');
}

if (clusterStatus.enabled && clusterStatus.running !== 'yes') {
  overallStatus = 'warning';
  issues.push('Cluster is enabled but not running properly');
}

if (issues.length > 2) {
  overallStatus = 'critical';
}

return {
  check_id: initData.check_id,
  timestamp: endTime,
  overall_status: overallStatus,
  wazuh_health: wazuhHealth,
  manager_status: managerStatus,
  cluster_status: clusterStatus,
  issues: issues,
  requires_attention: overallStatus !== 'healthy',
  check_duration_ms: responseTime
};

// Create alert notification for health issues
const healthData = $input.first().json;

const statusEmoji = {
  healthy: 'âœ…',
  warning: 'âš ï¸',
  degraded: 'ðŸ”¶',
  critical: 'ðŸš¨'
};

const statusColor = {
  healthy: '#00FF00',
  warning: '#FFFF00',
  degraded: '#FFA500',
  critical: '#FF0000'
};

const slackMessage = {
  channel: '#infrastructure-alerts',
  username: 'Wazuh Health Monitor',
  icon_emoji: ':shield:',
  attachments: [
    {
      color: statusColor[healthData.overall_status],
      title: `${statusEmoji[healthData.overall_status]} Wazuh API Health Alert - ${healthData.overall_status.toUpperCase()}`,
      fields: [
        {
          title: 'Check ID',
          value: healthData.check_id,
          short: true
        },
        {
          title: 'Wazuh Server',
          value: '172.20.18.14:55000',
          short: true
        },
        {
          title: 'Response Time',
          value: `${healthData.check_duration_ms}ms`,
          short: true
        },
        {
          title: 'API Status',
          value: healthData.wazuh_health.api_accessible ? 'Accessible' : 'Not Accessible',
          short: true
        },
        {
          title: 'Manager Status',
          value: healthData.manager_status.status,
          short: true
        },
        {
          title: 'Cluster Running',
          value: healthData.cluster_status.running || 'N/A',
          short: true
        }
      ],
      footer: 'Wazuh Health Monitor',
      ts: Math.floor(new Date(healthData.timestamp).getTime() / 1000)
    }
  ]
};

if (healthData.issues.length > 0) {
  slackMessage.attachments[0].fields.push({
    title: 'Issues Detected',
    value: healthData.issues.map(issue => `â€¢ ${issue}`).join('\n'),
    short: false
  });
}

return {
  slack_message: slackMessage,
  alert_sent: true,
  health_status: healthData.overall_status
};

// Attempt automatic remediation for critical issues
const healthData = $input.first().json;

const remediationActions = [];

// Check if API connectivity issues need attention
if (!healthData.wazuh_health.api_accessible) {
  remediationActions.push({
    action: 'check_network_connectivity',
    reason: 'Wazuh API is not accessible',
    priority: 'high'
  });
}

// Check if manager restart is needed
if (healthData.manager_status.status !== 'running') {
  remediationActions.push({
    action: 'restart_wazuh_manager',
    reason: 'Wazuh manager is not running',
    priority: 'high'
  });
}

// Check if cluster needs attention
if (healthData.cluster_status.enabled && healthData.cluster_status.running !== 'yes') {
  remediationActions.push({
    action: 'check_cluster_status',
    reason: 'Cluster is not running properly',
    priority: 'medium'
  });
}

return {
  remediation_required: remediationActions.length > 0,
  remediation_actions: remediationActions,
  health_data: healthData,
  auto_remediation_timestamp: new Date().toISOString()
};

// Log health check results
const healthData = $input.first().json;

const logEntry = {
  timestamp: healthData.timestamp,
  check_id: healthData.check_id,
  overall_status: healthData.overall_status,
  wazuh_response_time: healthData.check_duration_ms,
  api_accessible: healthData.wazuh_health.api_accessible,
  manager_status: healthData.manager_status.status,
  cluster_running: healthData.cluster_status.running,
  issues_count: healthData.issues.length,
  requires_attention: healthData.requires_attention
};

console.log(`Wazuh health check completed: ${healthData.check_id} - Status: ${healthData.overall_status}`);

if (healthData.issues.length > 0) {
  console.log(`Issues detected: ${healthData.issues.join(', ')}`);
}

return {
  health_check_complete: true,
  log_entry: logEntry,
  next_check_in: '5 minutes'
};

// Handle health check errors
const error = $input.first();
const initData = $('Initialize Health Check').first()?.json;

console.error('Wazuh health check failed:', error);

const errorReport = {
  check_id: initData?.check_id || 'unknown',
  error_timestamp: new Date().toISOString(),
  error_type: 'health_check_failure',
  error_message: error.message || 'Unknown error',
  wazuh_server: '172.20.18.14:55000',
  status: 'error',
  requires_manual_intervention: true
};

// Create critical alert for health check failure
const criticalAlert = {
  channel: '#critical-alerts',
  username: 'Wazuh Monitor CRITICAL',
  icon_emoji: ':rotating_light:',
  text: 'ðŸš¨ CRITICAL: Wazuh API Health Check Failed',
  attachments: [
    {
      color: '#FF0000',
      title: 'Wazuh API Health Check Failure',
      fields: [
        {
          title: 'Error',
          value: errorReport.error_message,
          short: false
        },
        {
          title: 'Wazuh Server',
          value: '172.20.18.14:55000',
          short: true
        },
        {
          title: 'Check ID',
          value: errorReport.check_id,
          short: true
        }
      ],
      footer: 'IMMEDIATE ACTION REQUIRED'
    }
  ]
};

return {
  error_report: errorReport,
  critical_alert: criticalAlert,
  health_check_failed: true
};
