// Validate incident data and prepare response actions
const incident = $input.first().json;

if (!incident.alert_id || !incident.ai_analysis) {
  throw new Error('Invalid incident data: missing alert_id or ai_analysis');
}

const actions = incident.ai_analysis.recommended_actions || [];
const threatLevel = incident.ai_analysis.threat_level || 'medium';
const sourceIP = incident.agent_ip || incident.threat_indicators?.source_ip;

// Prepare incident response plan
const responseActions = {
  block_ip: actions.includes('block_ip'),
  send_notification: actions.includes('send_notification') || threatLevel === 'critical',
  manual_investigation: actions.includes('manual_investigation'),
  quarantine_host: actions.includes('quarantine_host') || threatLevel === 'critical',
  collect_forensics: threatLevel === 'critical' || threatLevel === 'high'
};

return {
  ...incident,
  response_plan: responseActions,
  source_ip: sourceIP,
  incident_id: `INC-${Date.now()}-${incident.alert_id}`,
  response_timestamp: new Date().toISOString(),
  escalation_required: threatLevel === 'critical'
};

// Create comprehensive security notification
const incident = $input.first().json;
const aiAnalysis = incident.ai_analysis;
const responsePlan = incident.response_plan;

// Determine notification urgency
const urgency = incident.escalation_required ? 'CRITICAL' : 'HIGH';
const color = incident.escalation_required ? '#FF0000' : '#FFA500';

// Create detailed notification
const notification = {
  channel: '#security-alerts',
  username: 'Wazuh Security Bot',
  icon_emoji: ':warning:',
  attachments: [
    {
      color: color,
      title: `${urgency} Security Incident - ${incident.incident_id}`,
      title_link: `https://wazuh.company.com/alerts/${incident.alert_id}`,
      fields: [
        {
          title: 'Alert ID',
          value: incident.alert_id,
          short: true
        },
        {
          title: 'Threat Level',
          value: aiAnalysis.threat_level.toUpperCase(),
          short: true
        },
        {
          title: 'Source IP',
          value: incident.source_ip || 'Unknown',
          short: true
        },
        {
          title: 'Affected Agent',
          value: incident.agent_name || 'Unknown',
          short: true
        },
        {
          title: 'Rule Description',
          value: incident.rule_description,
          short: false
        },
        {
          title: 'AI Analysis',
          value: aiAnalysis.analysis_text.substring(0, 300) + '...',
          short: false
        }
      ],
      footer: 'Wazuh SIEM',
      ts: Math.floor(Date.now() / 1000)
    }
  ]
};

// Add actions taken
const actionsTaken = [];
if (responsePlan.block_ip) actionsTaken.push('ðŸš« IP Blocked');
if (responsePlan.quarantine_host) actionsTaken.push('ðŸ”’ Host Quarantined');
if (responsePlan.collect_forensics) actionsTaken.push('ðŸ” Forensics Collected');

if (actionsTaken.length > 0) {
  notification.attachments[0].fields.push({
    title: 'Automated Actions Taken',
    value: actionsTaken.join('\n'),
    short: false
  });
}

return {
  notification: notification,
  incident_summary: {
    incident_id: incident.incident_id,
    alert_id: incident.alert_id,
    threat_level: aiAnalysis.threat_level,
    actions_taken: actionsTaken,
    notification_sent: true,
    timestamp: new Date().toISOString()
  }
};

// Create incident ticket in ticketing system
const incident = $input.first().json;
const aiAnalysis = incident.ai_analysis;

const ticketData = {
  title: `Security Incident: ${incident.rule_description}`,
  description: `**Incident ID:** ${incident.incident_id}\n**Alert ID:** ${incident.alert_id}\n**Threat Level:** ${aiAnalysis.threat_level}\n**Source IP:** ${incident.source_ip}\n**Affected Agent:** ${incident.agent_name}\n\n**AI Analysis:**\n${aiAnalysis.analysis_text}\n\n**Recommended Actions:**\n${aiAnalysis.recommended_actions.join(', ')}\n\n**Automated Actions Taken:**\n- IP Blocking: ${incident.response_plan.block_ip ? 'Yes' : 'No'}\n- Host Quarantine: ${incident.response_plan.quarantine_host ? 'Yes' : 'No'}\n- Forensics Collection: ${incident.response_plan.collect_forensics ? 'Yes' : 'No'}`,
  priority: aiAnalysis.threat_level === 'critical' ? 'P1' : 'P2',
  category: 'Security Incident',
  subcategory: 'Automated Detection',
  assignee: 'security-team',
  tags: ['wazuh', 'automated', 'ai-analysis', aiAnalysis.threat_level]
};

return {
  ticket: ticketData,
  incident_id: incident.incident_id,
  requires_manual_review: incident.response_plan.manual_investigation
};

// Compile final incident response report
const inputs = $input.all();
const originalIncident = $('Prepare Response Plan').first().json;

// Collect results from all response actions
let ipBlocked = false;
let hostQuarantined = false;
let forensicsCollected = false;
let notificationSent = false;
let ticketCreated = false;

// Process results from different branches
inputs.forEach(input => {
  if (input.json.ip_blocked) ipBlocked = true;
  if (input.json.host_quarantined) hostQuarantined = true;
  if (input.json.forensics_collected) forensicsCollected = true;
  if (input.json.notification_sent) notificationSent = true;
  if (input.json.ticket_created) ticketCreated = true;
});

const responseReport = {
  incident_id: originalIncident.incident_id,
  alert_id: originalIncident.alert_id,
  response_timestamp: originalIncident.response_timestamp,
  completion_timestamp: new Date().toISOString(),
  threat_level: originalIncident.ai_analysis.threat_level,
  actions_completed: {
    ip_blocked: ipBlocked,
    host_quarantined: hostQuarantined,
    forensics_collected: forensicsCollected,
    notification_sent: notificationSent,
    ticket_created: ticketCreated
  },
  escalation_required: originalIncident.escalation_required,
  manual_review_required: originalIncident.response_plan.manual_investigation,
  response_status: 'completed',
  processing_duration: Date.now() - new Date(originalIncident.response_timestamp).getTime()
};

console.log(`Incident response completed for ${originalIncident.incident_id}`);
console.log(`Actions taken: ${Object.entries(responseReport.actions_completed).filter(([k,v]) => v).map(([k,v]) => k).join(', ')}`);

return responseReport;
