// Validate and normalize incoming Wazuh alert
const rawAlert = $input.first().json;

// Validate required fields
if (!rawAlert.rule || !rawAlert.rule.id) {
  throw new Error('Invalid Wazuh alert: missing rule information');
}

// Extract and normalize alert data
const alert = {
  alert_id: rawAlert.id || `alert-${Date.now()}`,
  timestamp: rawAlert.timestamp || new Date().toISOString(),
  rule_id: rawAlert.rule.id,
  rule_description: rawAlert.rule.description || 'Unknown rule',
  rule_level: rawAlert.rule.level || 0,
  agent_id: rawAlert.agent?.id || 'unknown',
  agent_name: rawAlert.agent?.name || 'unknown',
  agent_ip: rawAlert.agent?.ip || 'unknown',
  location: rawAlert.location || 'unknown',
  full_log: rawAlert.full_log || '',
  decoder_name: rawAlert.decoder?.name || 'unknown',
  data: rawAlert.data || {},
  previous_output: rawAlert.previous_output || '',
  syscheck: rawAlert.syscheck || null,
  rootcheck: rawAlert.rootcheck || null
};

// Determine alert severity and priority
let severity = 'low';
let priority = 'normal';
let requiresImmediateAction = false;

if (alert.rule_level >= 12) {
  severity = 'critical';
  priority = 'high';
  requiresImmediateAction = true;
} else if (alert.rule_level >= 7) {
  severity = 'high';
  priority = 'high';
  requiresImmediateAction = true;
} else if (alert.rule_level >= 4) {
  severity = 'medium';
  priority = 'normal';
}

// Check for specific high-priority indicators
const highPriorityKeywords = ['attack', 'intrusion', 'malware', 'rootkit', 'exploit', 'breach', 'unauthorized'];
const ruleDesc = alert.rule_description.toLowerCase();
if (highPriorityKeywords.some(keyword => ruleDesc.includes(keyword))) {
  requiresImmediateAction = true;
  if (severity === 'low') severity = 'medium';
}

return {
  ...alert,
  severity: severity,
  priority: priority,
  requires_immediate_action: requiresImmediateAction,
  received_at: new Date().toISOString(),
  processing_status: 'received',
  source: 'wazuh_webhook'
};

// Log alert processing and create summary
const alert = $input.first().json;
const isHighPriority = alert.requires_immediate_action;

const logEntry = {
  timestamp: new Date().toISOString(),
  alert_id: alert.alert_id,
  rule_id: alert.rule_id,
  rule_description: alert.rule_description,
  severity: alert.severity,
  agent_name: alert.agent_name,
  processing_path: isHighPriority ? 'high_priority_workflow' : 'standard_buffer',
  response_time: Date.now() - new Date(alert.received_at).getTime()
};

console.log(`Wazuh alert processed: ${alert.alert_id} - ${alert.severity} severity - routed to ${logEntry.processing_path}`);

return {
  processing_summary: logEntry,
  alert_processed: true,
  routing_decision: isHighPriority ? 'immediate_processing' : 'buffered_processing'
};

// Create Slack notification for medium/high alerts
const alert = $('Normalize Alert').first().json;

const severityEmoji = {
  critical: 'ðŸš¨',
  high: 'âš ï¸',
  medium: 'ðŸ”¶',
  low: 'â„¹ï¸'
};

const severityColor = {
  critical: '#FF0000',
  high: '#FFA500',
  medium: '#FFFF00',
  low: '#00FF00'
};

const slackMessage = {
  channel: '#security-monitoring',
  username: 'Wazuh Alert Bot',
  icon_emoji: ':shield:',
  attachments: [
    {
      color: severityColor[alert.severity],
      title: `${severityEmoji[alert.severity]} Wazuh Security Alert - ${alert.severity.toUpperCase()}`,
      fields: [
        {
          title: 'Alert ID',
          value: alert.alert_id,
          short: true
        },
        {
          title: 'Rule Level',
          value: alert.rule_level.toString(),
          short: true
        },
        {
          title: 'Agent',
          value: `${alert.agent_name} (${alert.agent_ip})`,
          short: true
        },
        {
          title: 'Location',
          value: alert.location,
          short: true
        },
        {
          title: 'Rule Description',
          value: alert.rule_description,
          short: false
        }
      ],
      footer: 'Wazuh SIEM',
      ts: Math.floor(new Date(alert.timestamp).getTime() / 1000)
    }
  ]
};

if (alert.full_log && alert.full_log.length > 0) {
  slackMessage.attachments[0].fields.push({
    title: 'Log Sample',
    value: `\`\`\`${alert.full_log.substring(0, 200)}...\`\`\``,
    short: false
  });
}

return {
  slack_message: slackMessage,
  notification_sent: true,
  alert_id: alert.alert_id
};

// Create final webhook response
const inputs = $input.all();
const originalAlert = $('Normalize Alert').first().json;

let highPriorityProcessed = false;
let buffered = false;
let notificationSent = false;

// Check results from different processing paths
inputs.forEach(input => {
  if (input.json.high_priority_processed) highPriorityProcessed = true;
  if (input.json.alert_buffered) buffered = true;
  if (input.json.notification_sent) notificationSent = true;
});

const response = {
  status: 'success',
  alert_id: originalAlert.alert_id,
  severity: originalAlert.severity,
  processing_path: originalAlert.requires_immediate_action ? 'high_priority' : 'standard',
  actions_taken: {
    high_priority_processed: highPriorityProcessed,
    buffered_for_polling: buffered,
    notification_sent: notificationSent
  },
  received_at: originalAlert.received_at,
  processed_at: new Date().toISOString(),
  message: `Alert ${originalAlert.alert_id} processed successfully via ${originalAlert.requires_immediate_action ? 'high-priority workflow' : 'standard buffering'}`
};

console.log(`Webhook processing complete for alert ${originalAlert.alert_id}`);

return response;

// Handle webhook processing errors
const error = $input.first();
const originalAlert = $('Normalize Alert').first()?.json;

console.error('Webhook processing error:', error);

const errorResponse = {
  status: 'error',
  alert_id: originalAlert?.alert_id || 'unknown',
  error_message: error.message || 'Unknown error occurred',
  timestamp: new Date().toISOString(),
  fallback_action: 'Alert logged for manual review'
};

// Log error for monitoring
console.log(`ERROR: Failed to process Wazuh webhook alert: ${errorResponse.error_message}`);

return errorResponse;
