// Normalize Wazuh alert safely (no throws)
const parseMaybeJson = (v)=>{ if(v==null) return null; if(typeof v==='object') return v; if(typeof v==='string'){ try { return JSON.parse(v); } catch { return null; } } return null; };
const toStr=(v,d='unknown')=> (v===undefined||v===null?d:String(v));
const toNum=(v,d=0)=>{ const n=Number(v); return Number.isFinite(n)?n:d; };
const raw = $input.first();
const item = raw?.json ?? {};
let payload = ('body' in item) ? item.body : item;
const parsed = parseMaybeJson(payload);
if (parsed) payload = parsed;
if (payload && typeof payload==='object' && 'alert' in payload) {
  const maybe = parseMaybeJson(payload.alert) ?? payload.alert;
  if (maybe && typeof maybe==='object') payload = { ...payload, ...maybe };
}
const ruleObj = (payload && typeof payload==='object' && payload.rule && typeof payload.rule==='object') ? payload.rule : {};
const agentObj = (payload && typeof payload==='object' && payload.agent && typeof payload.agent==='object') ? payload.agent : {};
const rule_id = toStr(ruleObj.id ?? ruleObj.rule_id ?? payload.rule_id ?? payload.id ?? 'custom', 'custom');
nconst rule_level = toNum(ruleObj.level ?? ruleObj.rule_level ?? payload.rule_level ?? payload.level ?? 0, 0);
const rule_description = toStr(ruleObj.description ?? ruleObj.rule_description ?? payload.rule_description ?? payload.description ?? 'Unknown rule', 'Unknown rule');
const normalized = {
  alert_id: toStr(payload.id ?? `alert-${Date.now()}`),
  timestamp: toStr(payload.timestamp ?? new Date().toISOString()),
  rule_id,
  rule_description,
  rule_level,
  agent_id: toStr(agentObj.id),
  agent_name: toStr(agentObj.name),
  agent_ip: toStr(agentObj.ip),
  location: toStr(payload.location,''),
  full_log: toStr(payload.full_log,''),
  decoder_name: toStr(payload?.decoder?.name,''),
  data: (payload && typeof payload.data==='object') ? payload.data : {},
  previous_output: toStr(payload.previous_output,''),
  syscheck: (payload && 'syscheck' in payload) ? payload.syscheck : null,
  rootcheck: (payload && 'rootcheck' in payload) ? payload.rootcheck : null,
};
let severity = 'low'; let priority = 'normal'; let requiresImmediateAction = false;
if (normalized.rule_level >= 12) { severity='critical'; priority='high'; requiresImmediateAction=true; }
else if (normalized.rule_level >= 7) { severity='high'; priority='high'; requiresImmediateAction=true; }
else if (normalized.rule_level >= 4) { severity='medium'; }
const descLC = normalized.rule_description.toLowerCase();
const keywords=['attack','intrusion','malware','rootkit','exploit','breach','unauthorized'];
if (keywords.some(k=>descLC.includes(k))) { requiresImmediateAction = true; if (severity==='low') severity='medium'; }
const validation_notes = [];
if (!ruleObj && !('level' in (payload||{})) && !('rule_level' in (payload||{}))) validation_notes.push('No explicit rule object; mapped from flat fields.');
if (!ruleObj?.id && !payload?.rule_id && !payload?.id) validation_notes.push('No rule id present; defaulted to "custom".');
return { ...normalized, severity, priority, requires_immediate_action: requiresImmediateAction, received_at: new Date().toISOString(), processing_status: 'received', source: 'wazuh_webhook', validation_notes };

// Build Teams-safe HTML body
const a = $json.alert;
const sev = (a.severity||'low').toLowerCase();
const emoji = { critical:'ðŸš¨', high:'âš ï¸', medium:'ðŸ”¶', low:'â„¹ï¸' };
const title = `${emoji[sev]||'â„¹ï¸'} Wazuh ${sev.toUpperCase()} alert`;
const esc = (s)=>String(s||'').replace(/[&<>]/g,c=>({"&":"&amp;","<":"&lt;",">":"&gt;"}[c]));
let html = `<b>${esc(title)}</b><br/>`+
  `<b>Alert ID:</b> ${esc(a.alert_id)}<br/>`+
  `<b>Rule Level:</b> ${esc(a.rule_level)}<br/>`+
  `<b>Agent:</b> ${esc(a.agent_name)} (${esc(a.agent_ip)})<br/>`+
  `<b>Location:</b> ${esc(a.location)}<br/>`+
  `<b>Rule:</b> ${esc(a.rule_description)}<br/>`+
  `<b>Timestamp:</b> ${esc(a.timestamp)}<br/>`;
if(a.full_log){
  const sample = String(a.full_log).slice(0,200) + (a.full_log.length>200?'â€¦':'');
  html += `<br/><b>Log Sample:</b><br/><pre>${esc(sample)}</pre>`;
}
return { json: { contentType: 'html', content: html, severity: sev } };

// pick the team with displayName === 'Wazoo'
const teams = $json.value || [];
const team = teams.find(t => (t.displayName||'').toLowerCase() === 'wazoo');
if(!team){
  throw new Error('Team "Wazoo" not found. Ensure the signed-in user is a member, or broaden Graph scope.');
}
return { teamId: team.id };

// pick channel with displayName === 'Wazoo notification'
const channels = $json.value || [];
const ch = channels.find(c => (c.displayName||'').toLowerCase() === 'wazoo notification');
if(!ch){
  throw new Error('Channel "Wazoo notification" not found in team Wazoo.');
}
return { teamId: $('Select Team ID').first().json.teamId, channelId: ch.id };
