// Initialize health check session
const checkId = `health-${Date.now()}`;
const timestamp = new Date().toISOString();

console.log(`Starting Wazuh API health check: ${checkId}`);

return {
  check_id: checkId,
  start_time: timestamp,
  wazuh_server: ($env.WAZUH_API_URL || 'https://172.20.18.14:55000').replace('https://','').replace(/\/$/, ''),
  check_type: 'scheduled_health_monitoring'
};

// Process health check results safely
const safeJson = (name) => { try { return $(name).first().json || null; } catch { return null; } };

const initData    = safeJson('Initialize Health Check') || {};
const clusterResp = safeJson('Wazuh Cluster Status');     // /cluster/status
const mgrStatus   = safeJson('Check Wazuh Connectivity'); // /manager/status

const endTime = new Date().toISOString();
const startTs = initData.start_time ? new Date(initData.start_time).getTime() : Date.now();
const responseTime = Date.now() - startTs;

const clusterData = clusterResp?.data || null; // { status: 'running' | ... }
const managerData = mgrStatus?.data || null;   // { status: 'running' | ... }

const apiAccessible = !!clusterResp || !!mgrStatus;

const wazuhHealth = {
  status: clusterData?.status || (apiAccessible ? 'unknown' : 'unreachable'),
  api_accessible: apiAccessible,
  response_time_ms: responseTime,
};

const managerStatus = {
  status: managerData?.status || 'unknown',
  version: null,
  compilation_date: null,
  api_response_time: responseTime,
};

let overallStatus = 'healthy';
const issues = [];

if (!apiAccessible) {
  overallStatus = 'critical';
  issues.push('Wazuh API is not accessible');
}

if ((managerStatus.status || 'unknown') !== 'running') {
  overallStatus = overallStatus === 'critical' ? 'critical' : 'degraded';
  issues.push('Wazuh manager is not running');
}

if (responseTime > 5000) {
  if (overallStatus === 'healthy') overallStatus = 'warning';
  issues.push('Slow API response time detected');
}

if (issues.length > 2) {
  overallStatus = 'critical';
}

const out = {
  check_id: initData.check_id || `health-${Date.now()}`,
  start_time: initData.start_time || new Date(Date.now() - responseTime).toISOString(),
  timestamp: endTime,
  wazuh_server: initData.wazuh_server || '172.20.18.14:55000',
  check_type: initData.check_type || 'scheduled_health_monitoring',
  overall_status: overallStatus,
  wazuh_health: wazuhHealth,
  manager_status: managerStatus,
  cluster_status: {
    status: clusterData?.status || 'unknown',
    running: clusterData?.status === 'running' ? 'yes' : 'no',
    enabled: true
  },
  issues,
  requires_attention: overallStatus !== 'healthy',
  check_duration_ms: responseTime,
};

return [{ json: out }];

// Create alert notification for health issues
const healthData = $input.first().json;

const statusEmoji = { healthy: 'âœ…', warning: 'âš ï¸', degraded: 'ðŸ”¶', critical: 'ðŸš¨' };
const statusColor = { healthy: '#00FF00', warning: '#FFFF00', degraded: '#FFA500', critical: '#FF0000' };

const slackMessage = {
  channel: '#infrastructure-alerts',
  username: 'Wazuh Health Monitor',
  icon_emoji: ':shield:',
  attachments: [
    {
      color: statusColor[healthData.overall_status],
      title: `${statusEmoji[healthData.overall_status]} Wazuh API Health Alert - ${healthData.overall_status.toUpperCase()}`,
      fields: [
        { title: 'Check ID', value: healthData.check_id, short: true },
        { title: 'Wazuh Server', value: healthData.wazuh_server, short: true },
        { title: 'Response Time', value: `${healthData.check_duration_ms}ms`, short: true },
        { title: 'API Status', value: healthData.wazuh_health.api_accessible ? 'Accessible' : 'Not Accessible', short: true },
        { title: 'Manager Status', value: healthData.manager_status.status, short: true },
        { title: 'Cluster Running', value: healthData.cluster_status.running || 'N/A', short: true }
      ],
      footer: 'Wazuh Health Monitor',
      ts: Math.floor(new Date(healthData.timestamp).getTime() / 1000)
    }
  ]
};

if (healthData.issues.length > 0) {
  slackMessage.attachments[0].fields.push({
    title: 'Issues Detected',
    value: healthData.issues.map(i => `â€¢ ${i}`).join('\n'),
    short: false
  });
}

return { slack_message: slackMessage, alert_sent: true, health_status: healthData.overall_status };

// Attempt automatic remediation for critical issues
const healthData = $input.first().json;

const remediationActions = [];

if (!healthData.wazuh_health.api_accessible) {
  remediationActions.push({ action: 'check_network_connectivity', reason: 'Wazuh API is not accessible', priority: 'high' });
}

if (healthData.manager_status.status !== 'running') {
  remediationActions.push({ action: 'restart_wazuh_manager', reason: 'Wazuh manager is not running', priority: 'high' });
}

if (healthData.cluster_status.enabled && healthData.cluster_status.running !== 'yes') {
  remediationActions.push({ action: 'check_cluster_status', reason: 'Cluster is not running properly', priority: 'medium' });
}

return { remediation_required: remediationActions.length > 0, remediation_actions: remediationActions, health_data: healthData, auto_remediation_timestamp: new Date().toISOString() };

// Log health check results
const healthData = $input.first().json;

const logEntry = {
  timestamp: healthData.timestamp,
  check_id: healthData.check_id,
  overall_status: healthData.overall_status,
  wazuh_response_time: healthData.check_duration_ms,
  api_accessible: healthData.wazuh_health.api_accessible,
  manager_status: healthData.manager_status.status,
  cluster_running: healthData.cluster_status.running,
  issues_count: healthData.issues.length,
  requires_attention: healthData.requires_attention
};

console.log(`Wazuh health check completed: ${healthData.check_id} - Status: ${healthData.overall_status}`);
if (healthData.issues.length > 0) console.log(`Issues detected: ${healthData.issues.join(', ')}`);

return { health_check_complete: true, log_entry: logEntry, next_check_in: '5 minutes' };
